-- ----------------------------
-- Table structure for sys_addons
-- ----------------------------
DROP TABLE IF EXISTS `sys_addons`;
CREATE TABLE `sys_addons` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键',
  `name` varchar(40) NOT NULL COMMENT '插件名或标识',
  `title` varchar(20) NOT NULL DEFAULT '' COMMENT '中文名',
  `description` text COMMENT '插件描述',
  `status` tinyint(1) NOT NULL DEFAULT '1' COMMENT '状态',
  `config` text COMMENT '配置',
  `author` varchar(40) DEFAULT '' COMMENT '作者',
  `version` varchar(20) DEFAULT '' COMMENT '版本号',
  `has_adminlist` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否有后台列表',
  `has_addonslist` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否有插件列表',
  `content` text NOT NULL COMMENT '详情',
  `config_hook` varchar(255) NOT NULL DEFAULT '' COMMENT '自定义配置文件钩子',
  `create_time` int(11) DEFAULT '0' COMMENT '安装时间',
  `module_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '关联的入口菜单',
  `logo` varchar(255) NOT NULL DEFAULT '' COMMENT '应用图标',
  `sort` int(10) NOT NULL DEFAULT '0' COMMENT '排序',
  `admin_module_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '关联的admin端入口菜单',
  `config_admin_hook` varchar(255) NOT NULL DEFAULT '' COMMENT '自定义admin配置文件钩子',
  `no_set` tinyint(1) NOT NULL DEFAULT '0' COMMENT '不需要应用设置',
  `category_id` int(11) NOT NULL DEFAULT '0' COMMENT '分类id',
  `is_value_add` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否增值',
  `cycle_price` text COMMENT '周期价格',
  `up_status` int(11) NOT NULL DEFAULT '0' COMMENT '即将上线',
  `logo_small` varchar(255) NOT NULL DEFAULT '' COMMENT '应用图标(48*48)',
  `logo_often` varchar(255) NOT NULL DEFAULT '' COMMENT '应用图标(常用功能)',
  `is_code` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT '是否仅限源码购买,1是,0否',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=220 DEFAULT CHARSET=utf8 AVG_ROW_LENGTH=100 ROW_FORMAT=DYNAMIC COMMENT='插件表';

