INSERT INTO `sys_addons` VALUES ('160', 'distribution', '全网分销', '全网分销，下线下单上线返佣', '1', '[]', 'vslaishop', '1.0', '0', '1', '<p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><strong><span style=\"font-family: 微软雅黑;color: rgb(84, 141, 212);letter-spacing: 0;font-size: 16px\">简介：</span></strong></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px\"><span style=\"font-family:微软雅黑\">分销商通过一定的渠道把产品销售给消费者，在三级内的关系人产生订单可获得返佣。</span></span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><span style=\"letter-spacing: 0;font-size: 16px\">&nbsp;</span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><strong><span style=\"font-family: 微软雅黑;color: rgb(84, 141, 212);letter-spacing: 0;font-size: 16px\">名词解释：</span></strong></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px\"><span style=\"font-family:微软雅黑\">下线（粉丝）：指通过分销商二维码，或通过分销链接发展的客户，与分销商绑定上下级关系，但不具备分销商资格。</span></span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px\"><span style=\"font-family:微软雅黑\">分销商：通过申请、或达到您的分销条件的人，分销商可以获得相应的返佣</span></span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><span style=\"letter-spacing: 0;font-size: 16px\">&nbsp;</span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><strong><span style=\"font-family: 微软雅黑;color: rgb(84, 141, 212);letter-spacing: 0;font-size: 16px\">我们建议客户：</span></strong></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px\">1、分销佣金不高于50%；</span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px\">2、不设置加入门槛费；</span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px\">3、担心微信端受影响，可以考虑做全网版。</span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0;text-align:justify;text-justify:inter-ideograph\"><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px\">&nbsp;</span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0;text-align:justify;text-justify:inter-ideograph\"><strong><span style=\"font-family: 微软雅黑;color: rgb(84, 141, 212);letter-spacing: 0;font-size: 16px\">功能介绍：</span></strong></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0;text-align:justify;text-justify:inter-ideograph\"><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px\">1、</span><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px\"><span style=\"font-family:微软雅黑\">分销中心页面可自定义。</span></span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0;text-align:justify;text-justify:inter-ideograph\"><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px\">2、</span><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px\"><span style=\"font-family:微软雅黑\">佣金</span></span><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px\"><span style=\"font-family:微软雅黑\">比例可把控，</span></span><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px\"><span style=\"font-family:微软雅黑\">可设置</span></span><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px\"><span style=\"font-family:微软雅黑\">跳降级；</span></span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0;text-align:justify;text-justify:inter-ideograph\"><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px\">3、</span><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px\"><span style=\"font-family:微软雅黑\">不同</span></span><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px\"><span style=\"font-family:微软雅黑\">分销商</span></span><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px\"><span style=\"font-family:微软雅黑\">等级可享受不同的</span></span><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px\"><span style=\"font-family:微软雅黑\">佣金比例</span></span><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px\"><span style=\"font-family:微软雅黑\">；</span></span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0;text-align:justify;text-justify:inter-ideograph\"><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px\">4、</span><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px\"><span style=\"font-family:微软雅黑\">强大的分销管理后台，查看管理分析分销商数据；</span></span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0;text-align:justify;text-justify:inter-ideograph\"><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px\">5、</span><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px\"><span style=\"font-family:微软雅黑\">多种成为</span></span><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px\"><span style=\"font-family:微软雅黑\">分销商</span></span><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px\"><span style=\"font-family:微软雅黑\">的条件设置，自定义申请协议等。</span></span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0;text-align:justify;text-justify:inter-ideograph\"><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px\">6、</span><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px\"><span style=\"font-family:微软雅黑\">招募会员，不断裂变，让会员帮你分销，通过佣金激励会员</span>&nbsp;帮你推广传播产生订单；</span></p><p><br/></p>', 'distributionProfile', '1553943149', '14946', 'https://pic.vslai.com.cn/upload/common/1554197096.png', '0', '0', '', '0', '7', '0', '[]', '0', 'https://pic.vslai.com.cn/upload/common/1563781881.png', 'https://pic.vslai.com.cn/upload/common/1563781913.png', '0');
INSERT INTO `sys_addons` VALUES ('161', 'teambonus', '团队分红', '团队队长分红下线的所有订单', '1', '[]', 'vslaishop', '1.0', '0', '1', '<p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><strong><span style=\"font-family: 微软雅黑;color: rgb(84, 141, 212);letter-spacing: 0;font-size: 16px\">简介：</span></strong></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px\"><span style=\"font-family:微软雅黑\">团队分红：指拿所有下级、下线代理订单金额分红。</span></span><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px;background: rgb(255, 255, 255)\"><br/></span><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px;background: rgb(255, 255, 255)\"><span style=\"font-family:微软雅黑\">注：会员需先成为分销商才能成为分红代理商</span></span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><span style=\"letter-spacing: 0;font-size: 16px\">&nbsp;</span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><strong><span style=\"font-family: 微软雅黑;color: rgb(84, 141, 212);letter-spacing: 0;font-size: 16px;background: rgb(255, 255, 255)\"><span style=\"font-family:微软雅黑\">什么是分红模式？</span></span></strong><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px;background: rgb(255, 255, 255)\"><br/></span><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px;background: rgb(255, 255, 255)\"><span style=\"font-family:微软雅黑\">平台运营者将一定比例的产品利润用于奖励平台中业绩突出的推广者，并根据推广者的业绩评定相应股东等级，分配其团队业绩红利。</span></span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><span style=\"letter-spacing: 0;font-size: 16px\">&nbsp;</span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0;text-align:justify;text-justify:inter-ideograph\"><strong><span style=\"font-family: 微软雅黑;color: rgb(84, 141, 212);letter-spacing: 0;font-size: 16px\">功能介绍：</span></strong></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0;text-align:justify;text-justify:inter-ideograph\"><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px\">1、打通线下代理渠道，无缝对接线上电商；</span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0;text-align:justify;text-justify:inter-ideograph\"><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px\">2、不同队长等级可享受不同的分红返利；</span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0;text-align:justify;text-justify:inter-ideograph\"><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px\">3、强大的队长管理后台，查看管理分析分销商数据；</span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0;text-align:justify;text-justify:inter-ideograph\"><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px\">4、分红比例可把控，分红内购、极差、跳降级设置等；</span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0;text-align:justify;text-justify:inter-ideograph\"><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px\">5、多种成为队长的条件设置，自定义申请协议等。</span></p><p><br/></p>', 'teamBonusProfile', '1553943171', '14965', 'https://pic.vslai.com.cn/upload/common/1554197101.png', '0', '0', '', '0', '7', '0', '[]', '0', 'https://pic.vslai.com.cn/upload/common/1563782158.png', 'https://pic.vslai.com.cn/upload/common/1563782281.png', '0');
