INSERT INTO `sys_addons` VALUES ('169', 'store', 'O2O门店', '线上消费线下体验核销', '1', '[]', 'vslaishop', '1.0', '0', '1', '<p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><strong><span style=\"font-family: 微软雅黑;color: rgb(84, 141, 212);letter-spacing: 0;font-size: 16px\">简介：</span></strong></p><p><span style=\";font-family:微软雅黑;font-size:14px\"><span style=\"font-family:微软雅黑\">若有营业门店，则视为支持</span>O2O服务。 </span><span style=\"font-family: 微软雅黑;letter-spacing: 1px;font-size: 14px;background: rgb(255, 255, 255)\"><span style=\"font-family:微软雅黑\">客户在线上下单、到线下实体店取货，</span>O2O即Online To Offline（在线离线/线上到线下），是指将线下的商务机会与互联网结合，让互联网成为线下交易的平台。</span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><strong><span style=\"font-family: 微软雅黑;color: rgb(84, 141, 212);letter-spacing: 0;font-size: 16px\">&nbsp;</span></strong></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0;text-align:justify;text-justify:inter-ideograph\"><strong><span style=\"font-family: 微软雅黑;color: rgb(84, 141, 212);letter-spacing: 0;font-size: 16px\">功能介绍：</span></strong></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0;text-align:justify;text-justify:inter-ideograph\"><span style=\"font-family: 微软雅黑;font-size: 14px\">1、</span><span style=\"font-family: 微软雅黑;font-size: 14px\"><span style=\"font-family:微软雅黑\">若为营业状态，则视为支持</span>O2O服务；</span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0;text-align:justify;text-justify:inter-ideograph\"><span style=\"font-family: 微软雅黑;font-size: 14px\">2、</span><span style=\"font-family: 微软雅黑;font-size: 14px\"><span style=\"font-family:微软雅黑\">店员拥有自己的店员端后台，登录后可进行核销订单、核销礼品、核销消费卡等操作；</span></span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0;text-align:justify;text-justify:inter-ideograph\"><span style=\"font-family: 微软雅黑;font-size: 14px\">3、</span><span style=\"font-family: 微软雅黑;font-size: 14px\"><span style=\"font-family:微软雅黑\">可根据实际情况划分岗位权限，控制店员在</span>O2O店员端里面的权限。</span></p><p><br/></p>', 'storeList', '1553943311', '15087', 'https://pic.vslai.com.cn/upload/common/1554196943.png', '2', '15100', 'storeList', '1', '6', '0', '[]', '0', 'https://pic.vslai.com.cn/upload/common/1563782074.png', 'https://pic.vslai.com.cn/upload/common/1563782208.png', '0');
