INSERT INTO `sys_addons` VALUES ('187', 'bargain', '砍价', '邀请好友砍价，病毒式宣传', '1', '[]', 'vslaishop', '1.0', '0', '1', '<p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><strong><span style=\"font-family: 微软雅黑;color: rgb(84, 141, 212);letter-spacing: 0;font-size: 16px\">简介：</span></strong></p><p><span style=\";font-family:微软雅黑;font-size:14px\"><span style=\"font-family:微软雅黑\">买卖商品是卖方在售价基础上设置低价，通过买家砍价以达到买家满意价位的行为。</span></span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0\"><strong><span style=\"font-family: 微软雅黑;color: rgb(84, 141, 212);letter-spacing: 0;font-size: 16px\">&nbsp;</span></strong></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0;text-align:justify;text-justify:inter-ideograph\"><strong><span style=\"font-family: 微软雅黑;color: rgb(84, 141, 212);letter-spacing: 0;font-size: 16px\">功能介绍：</span></strong></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0;text-align:justify;text-justify:inter-ideograph\"><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px;background: rgb(255, 255, 255)\">1、</span><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px;background: rgb(255, 255, 255)\"><span style=\"font-family:微软雅黑\">用户通过砍价功能，可以进行邀请好友砍价</span></span><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px;background: rgb(255, 255, 255)\"><span style=\"font-family:微软雅黑\">；</span></span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0;text-align:justify;text-justify:inter-ideograph\"><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px;background: rgb(255, 255, 255)\">2、</span><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px;background: rgb(255, 255, 255)\"><span style=\"font-family:微软雅黑\">用户砍掉价格后，不仅能提高店面的线下流量，而且还能够获得精准的客户</span></span><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px;background: rgb(255, 255, 255)\"><span style=\"font-family:微软雅黑\">；</span></span></p><p style=\"margin-top:5px;margin-right:0;margin-bottom:5px;margin-left:0;text-indent:0;text-align:justify;text-justify:inter-ideograph\"><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px;background: rgb(255, 255, 255)\">3、</span><span style=\"font-family: 微软雅黑;letter-spacing: 0;font-size: 14px;background: rgb(255, 255, 255)\"><span style=\"font-family:微软雅黑\">通过砍价活动，可以使商品宣传急速裂变，一传二、二传四、四传八</span>……指数扩大宣传规模，大幅增加线下引流。</span></p><p><br/></p>', 'bargainList', '1553943610', '15326', 'https://pic.vslai.com.cn/upload/common/1554197071.png', '0', '15333', 'bargainList', '0', '4', '0', '[]', '0', 'https://pic.vslai.com.cn/upload/common/1563782121.png', 'https://pic.vslai.com.cn/upload/common/1563782243.png', '0');
