-- ----------------------------
-- Table structure for sys_authorization
-- ----------------------------
DROP TABLE IF EXISTS `sys_authorization`;
CREATE TABLE `sys_authorization` (
  `authori_id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键ID',
  `company_name` varchar(255) NOT NULL COMMENT '公司名称',
  `real_name` varchar(255) DEFAULT NULL COMMENT '真实姓名',
  `user_tel` varchar(20) NOT NULL DEFAULT '' COMMENT '手机号',
  `domain` varchar(255) NOT NULL COMMENT '授权域名',
  `auth_versionid` int(10) NOT NULL DEFAULT '0' COMMENT '版本id',
  `expiry_time` int(11) DEFAULT '0' COMMENT '到期时间',
  `related_sales` int(11) NOT NULL DEFAULT '0' COMMENT '关联销售id',
  `related_operating` int(11) NOT NULL DEFAULT '0' COMMENT '关联运营id',
  `secret_key` varchar(255) NOT NULL DEFAULT '' COMMENT '源码秘钥',
  `note` varchar(500) NOT NULL COMMENT '备注',
  `create_time` int(11) DEFAULT '0' COMMENT '网站创建时间',
  `modify_time` int(11) DEFAULT '0' COMMENT '修改时间',
  `agent_id` int(11) DEFAULT NULL COMMENT '关联user表uid',
  `ismade` varchar(10) NOT NULL DEFAULT '否',
  PRIMARY KEY (`authori_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;

-- ----------------------------
-- Records of sys_authorization
-- ----------------------------

