  `is_default` smallint(1) NOT NULL DEFAULT '0' COMMENT '是否为默认',
  `is_system_default` smallint(1) NOT NULL DEFAULT '0' COMMENT '新建对象没有内容的时候就复制这里的几个系统默认',
  `in_use` smallint(1) NOT NULL DEFAULT '0' COMMENT '0:不在使用中,1:使用中',
