  PRIMARY KEY (`id`),
  KEY `rds_idx_0` (`website_id`,`type`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=10 DEFAULT CHARSET=utf8 AVG_ROW_LENGTH=16384 COMMENT='手机端自定义模板';

