-- ----------------------------
-- Table structure for sys_mp_custom_template
-- ----------------------------
DROP TABLE IF EXISTS `sys_mp_custom_template`;
CREATE TABLE `sys_mp_custom_template` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `shop_id` int(11) NOT NULL DEFAULT '0' COMMENT '店铺id',
  `template_name` varchar(250) DEFAULT NULL COMMENT '自定义模板名称（暂时预留）',
  `template_data` longtext NOT NULL COMMENT '模板数据（JSON格式）',
  `create_time` int(11) NOT NULL DEFAULT '0' COMMENT '创建时间戳',
  `modify_time` int(11) DEFAULT NULL COMMENT '修改时间戳',
  `website_id` int(11) NOT NULL DEFAULT '0' COMMENT '实例信息',
  `type` int(2) NOT NULL DEFAULT '1' COMMENT '1:商城首页 2:店铺首页  3:商品详情页 4:会员中心 5:分销中心，6:自定义页面,7:底部',
  `is_default` smallint(1) NOT NULL DEFAULT '0' COMMENT '是否为默认',
  `is_system_default` smallint(1) NOT NULL DEFAULT '0' COMMENT '新建对象没有内容的时候就复制这里的几个系统默认',
  `in_use` smallint(1) NOT NULL DEFAULT '0' COMMENT '0:不在使用中,1:使用中',
  `template_logo` varchar(255) DEFAULT NULL COMMENT '模板logo',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=9 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;

