  `first_bargain_money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '//第一刀金额',
  `bargain_method` tinyint(2) NOT NULL DEFAULT '1' COMMENT '//砍价方式 1-固定金额 2-随机金额',
