  `rand_highest_money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '//随机最高金额',
  `is_my_bargain` tinyint(2) NOT NULL DEFAULT '0' COMMENT '//我是否可以砍价 0-不可以 1-可以',
  `bargain_stock` int(10) NOT NULL DEFAULT '0' COMMENT '//活动库存',
  `limit_buy` int(10) NOT NULL DEFAULT '0' COMMENT '//限购',
  `bargain_sales` varchar(10) NOT NULL DEFAULT '0' COMMENT '//砍价销量',
  `goods_name` varchar(255) NOT NULL DEFAULT '' COMMENT '//商品名称',
  `picture` int(10) NOT NULL DEFAULT '0' COMMENT '//商品图片id',
  `website_id` int(10) NOT NULL DEFAULT '0',
  `shop_id` int(10) NOT NULL DEFAULT '0',
  `create_time` int(10) NOT NULL DEFAULT '0' COMMENT '//创建时间',
  `update_time` int(10) NOT NULL DEFAULT '0' COMMENT '//更新时间',
  `past_change_goods_promotion_status` tinyint(2) NOT NULL DEFAULT '0' COMMENT '//是否更改了过期商品的促销状态',
  `close_status` tinyint(2) NOT NULL DEFAULT '1' COMMENT '//活动是否开启-1 关闭-0',
  PRIMARY KEY (`bargain_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;

