  `shop_id` int(11) NOT NULL COMMENT '店铺ID',
  `festivalcare_name` varchar(100) NOT NULL DEFAULT '' COMMENT '活动名称',
  `create_time` int(11) NOT NULL DEFAULT '0' COMMENT '创建时间',
