  `is_auto_take_task` tinyint(2) NOT NULL DEFAULT '0' COMMENT '//是否自动接任务0-关闭 1-开启',
  `is_repeat_take_task` tinyint(2) NOT NULL DEFAULT '0' COMMENT '//是否可重复领取0-关闭1-开启',
