  `push_cover` varchar(255) NOT NULL DEFAULT '' COMMENT '//推送封面',
  `push_desc` varchar(255) NOT NULL DEFAULT '' COMMENT '//推送描述',
  `push_link` varchar(255) NOT NULL DEFAULT '' COMMENT '//推送链接',
  `push_text` text NOT NULL COMMENT '//推送文本',
  `perm_setting` tinyint(2) NOT NULL DEFAULT '0' COMMENT '//权限设置0-关闭1-开启',
  `customer_service_message` varchar(255) NOT NULL DEFAULT '' COMMENT '//未开放时客服消息',
  `explanation_link` varchar(255) NOT NULL DEFAULT '' COMMENT '//未开放时说明链接',
  `is_become_offline` tinyint(2) NOT NULL DEFAULT '0' COMMENT '// 是否扫码关注成为下线0-关闭1-开启',
  `is_become_distribution` tinyint(2) NOT NULL DEFAULT '0' COMMENT '//是否扫码关注成为分销商0-关闭1-开启',
  `task_type` tinyint(2) NOT NULL DEFAULT '0' COMMENT '//海报任务类型  0-普通任务 1-普通海报 2-多级海报',
  `shop_id` int(11) NOT NULL DEFAULT '0' COMMENT '//店铺id',
  `website_id` int(11) NOT NULL DEFAULT '0',
  `create_time` int(11) NOT NULL DEFAULT '0',
  `update_time` int(11) NOT NULL DEFAULT '0' COMMENT '//更新时间',
  PRIMARY KEY (`general_poster_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;

