  `browse_auth_u` varchar(255) DEFAULT NULL COMMENT '商品浏览权限(会员)',
  `browse_auth_d` varchar(255) DEFAULT NULL COMMENT '商品浏览权限(分销商)',
  `browse_auth_s` varchar(255) DEFAULT NULL COMMENT '商品浏览权限(会员分组)',
  `buy_auth_u` varchar(255) DEFAULT NULL COMMENT '商品购买权限(会员)',
  `buy_auth_d` varchar(255) DEFAULT NULL COMMENT '商品购买权限(分销商)',
  `buy_auth_s` varchar(255) DEFAULT NULL COMMENT '商品购买权限(会员分组)',
  `channel_auth` varchar(255) DEFAULT '' COMMENT '渠道商权限',
  PRIMARY KEY (`id`),
  KEY `goods_id` (`goods_id`) USING BTREE COMMENT '商品id'
) ENGINE=MyISAM DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

