  `is_refund` tinyint(1) DEFAULT NULL COMMENT '是否退款',
  `status` tinyint(1) DEFAULT NULL COMMENT '发票状态 0未执行，1成功 2失败 3等待中 4处理中 5部分完成',
  `tax` decimal(10,2) DEFAULT NULL COMMENT '发票税费（下单时）',
