  `openid` varchar(125) DEFAULT NULL COMMENT '获得发票用户的openid',
  `unionid` varchar(125) DEFAULT NULL COMMENT '微信unionid',
  `s_pappid` varchar(125) DEFAULT NULL COMMENT '开票平台识别码',
  `user_auth_time` int(11) DEFAULT NULL COMMENT '领取发票时间（即授权链接时间，未成插入微信卡包前）',
