  `auth_ticket` varchar(255) DEFAULT NULL COMMENT '授权页ticket凭证json',
  `is_again` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否重新开票 0否 1是',
  `is_auth` tinyint(1) DEFAULT NULL COMMENT '用户是否授权0否 1是',
